<script>
    import { scrollTo as animatedScroll } from 'svelte-scrollto'

    export let btn = true
    export let fill = false
    export let small = false
    export let outline = false
    export let featured = false
    export let duration = 2000
    export { className as class }
    export { element as scrollTo }

    let className = ''
    let element = false

    const scrollTo = () => element ? animatedScroll({ element, duration }) : null
</script>

<button class={className} class:btn class:featured class:small class:fill class:outline on:click={scrollTo}>
    <slot />
</button>

<style global>
    .btn {
        @apply px-4 py-4 text-lg font-bold transition duration-500 ease-out border rounded outline-none cursor-pointer select-none border-prime;
        -webkit-tap-highlight-color: transparent;
    }

    .btn:active {
        @apply transition duration-200 ease-in-out transform scale-95;
    }

    .outline {
        @apply bg-transparent border text-secondary hover:text-white hover:bg-prime;
    }

    .fill {
        @apply text-white bg-prime hover:bg-primary;
    }

    .small {
        @apply px-2 py-2;
    }

    .featured {
        @apply md:px-6 md:py-6; 
    }
</style>
