<script>
    import getBaseURL from 'utils/baseURL'

    export let name
    export let center = false
    export { className as class }

    let className = ''

    const getName = () => name.charAt(0).toUpperCase() + name.slice(1)
</script>

<img class:center class={className} src="{getBaseURL()}/icons/{name}.svg" alt={getName()}>

<style>
    .center {
        @apply mx-auto;
    }
</style>
