<script>
    import getBaseURL from 'utils/baseURL'

    const IMG_404 = getBaseURL() + '/images/not_found.png'
    const TITLE_404 = 'Image Not Found'

    export let src = IMG_404
    export let title = TITLE_404
    export { className as class }

    let className = ''
    let alt = title

    function errorHandler() {
        this.onerror = null
        this.src = IMG_404
        this.title = TITLE_404
    }
</script>

<img loading="lazy" class={className} {src} {title} {alt} on:error={errorHandler} />

<style>
    img {
        @apply rounded max-w-300 max-h-180 md:w-full md:max-h-none md:max-w-none;
    }
</style>
